// components/SkillForm.js

import React, { useState } from 'react';
import api from '../utils/api';

const SkillForm = ({ userId }) => { // Pass userId as props
  const [input, setInput] = useState('');
  const [error, setError] = useState(null);

  const onChange = (e) => setInput(e.target.value);

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token'); // Retrieve the user's token from local storage
      if (!token) {
        throw new Error('No token available'); // Throw an error if token is not available
      }
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // Include token in the request headers
        }
      };
      const data = { name: input };
      await api.post('/skills/add-skill', data, config); // Include token in the request headers
      setInput('');
      setError(null); // Reset error state
    } catch (err) {
      console.error('Error adding skill:', err); // Log the error for debugging
      setError('Failed to add skill');
    }
  };
  

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded shadow">
      <h2 className="text-2xl mb-6">Add Skill</h2>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Skill</label>
          <input
            type="text"
            value={input}
            onChange={onChange}
            className="w-full p-2 border border-gray-300 rounded"
            placeholder="Enter skill"
            required
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          Add Skill
        </button>
      </form>
    </div>
  );
};

export default SkillForm;
