import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Importing useNavigate instead of useHistory
import SkillForm from '../components/SkillForm';
import api from '../utils/api';

const Dashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Using useNavigate instead of useHistory

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    } else {
      navigate('/login'); // Using navigate to redirect to login page if not logged in
    }
  }, [navigate]); // Adding navigate to dependency array

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    navigate('/login'); // Using navigate to redirect to login page after logout
  };

  return (
    <div>
      <h1>Welcome to the Dashboard</h1>
      <button onClick={handleLogout}>Logout</button>
      {isLoggedIn && <SkillForm />}
    </div>
  );
};

export default Dashboard;
