import React, { useState } from 'react';
import api from '../utils/api';

const SkillForm = ({ userId }) => {
  const [input, setInput] = useState('');
  const [error, setError] = useState(null);

  const onChange = (e) => setInput(e.target.value);

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token
        }
      };
  
      // Log input value before splitting
      console.log('Input:', input);
  
      const skillsArray = input.split(',').map(skill => skill.trim());
      console.log('Skills Array:', skillsArray); // Log the skills array
      
      for (const skill of skillsArray) {
        console.log('Adding skill:', skill); // Log each skill before sending the request
        await api.post('/skills/add-skill', { name: skill, userId }, config);
        console.log('Skill added:', skill); // Log after adding the skill
      }
  
      setInput('');
      setError(null);
    } catch (err) {
      console.error('Error adding skill:', err); // Log any errors
      setError('Failed to add skill');
    }
  };
  
  
  

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded shadow">
      <h2 className="text-2xl mb-6">Add Skill</h2>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Skill</label>
          <input
            type="text"
            value={input}
            onChange={onChange}
            className="w-full p-2 border border-gray-300 rounded"
            placeholder="Enter skill"
            required
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          Add Skill
        </button>
      </form>
    </div>
  );
};

export default SkillForm;
