// src/components/SkillForm.js

import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const SkillForm = () => {
  const [skills, setSkills] = useState([]);
  const [input, setInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSkills = async () => {
      try {
        const res = await api.get('/skills');
        setSuggestions(res.data);
      } catch (err) {
        setError('Failed to fetch skills');
      }
    };
    fetchSkills();
  }, []);

  const onChange = (e) => {
    setInput(e.target.value);
    const filtered = suggestions.filter((skill) =>
      skill.name.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setSkills(filtered);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/skills/add-skill', { skills: input.split(',') }, {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      });
      setInput('');
      setSkills([]);
    } catch (err) {
      setError('Failed to add skills');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded shadow">
      <h2 className="text-2xl mb-6">Add Skills</h2>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Skills</label>
          <input
            type="text"
            value={input}
            onChange={onChange}
            className="w-full p-2 border border-gray-300 rounded"
            placeholder="Enter skills separated by commas"
            required
          />
          <ul className="bg-white border border-gray-300 rounded mt-2">
            {skills.map((skill) => (
              <li key={skill._id} className="p-2 hover:bg-gray-200 cursor-pointer">
                {skill.name}
              </li>
            ))}
          </ul>
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          Add Skills
        </button>
      </form>
    </div>
  );
};

export default SkillForm;
