import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import SkillForm from '../components/SkillForm';
import api from '../utils/api';

const Dashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const [userSkills, setUserSkills] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      fetchUserInfo(token);
      fetchUserSkills(token);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };
      const response = await api.get('/user', config); // Update the endpoint to '/user'
      setUserName(response.data); // Assuming setUser is a state setter function
    } catch (error) {
      console.error('Error fetching user info:', error);
      // Handle error (e.g., display an error message)
    }
  };
  

  const fetchUserSkills = async (token) => {
    try {
      const response = await api.get('/skills', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserSkills(response.data);
    } catch (error) {
      console.error('Error fetching user skills:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    navigate('/login');
  };

  return (
    <div>
      <h1>Welcome to the Dashboard, {userName}</h1>
      <button onClick={handleLogout}>Logout</button>
      {isLoggedIn && <SkillForm />}
      <h2>Your Skills:</h2>
      <ul>
        {userSkills.map(skill => (
          <li key={skill.id}>{skill.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
